            tag="sg2",
            pair=trade.pair,
            rate=exit_rate,
            stake_amount=sell_amount,
            profit_stake=profit_stake,
            profit_ratio=profit_ratio,
            stake_currency=self.config["stake_currency"],
            grind_profit_stake=grind_profit * sell_amount * trade.leverage,
